import React, { useEffect, useState } from 'react';
import axios from 'axios';
import EmployeeList from './components/EmployeeList';
import AddEmployee from './components/AddEmployee';
import UpdateEmployee from './components/UpdateEmployee';

function App() {
  const [employees, setEmployees] = useState([]);

  const fetchEmployees = () => {
    axios.get('http://127.0.0.1:5000/employees')
      .then(response => {
        setEmployees(response.data);
      })
      .catch(error => {
        console.error("There was an error fetching the employees!", error);
      });
  };

  useEffect(() => {
    fetchEmployees();
  }, []);


  const handleDeleteEmployee = (id) => {
    axios.delete(`http://127.0.0.1:5000/employee/${id}`)
      .then(response => {
        setEmployees(employees.filter(emp => emp.id !== id));
      })
      .catch(error => {
        console.error("There was an error deleting the employee!", error);
      });
  };

  return (
    <div className="App">
      <h1>Employee Management System</h1>
      <button type="button" class="btn btn-outline-light lg" id="button-try"><a href="/add">Add</a></button>
      <button type="button" class="btn btn-outline-light lg" id="button-try"><a href="/add">Update</a></button>
      <EmployeeList employees={employees} onDeleteEmployee={handleDeleteEmployee} />
    </div>
  );
}

export default App;
