import React, { useEffect, useState } from 'react';
import { BrowserRouter , Router, Route, Routes, Link } from 'react-router-dom';
import axios from 'axios';
import EmployeeList from './components/EmployeeList';
import AddEmployee from './components/AddEmployee';
import UpdateEmployee from './components/UpdateEmployee';

function App() {
  const [employees, setEmployees] = useState([]);

  const fetchEmployees = () => {
    axios.get('http://localhost:5000/employees')
      .then(response => {
        setEmployees(response.data);
      })
      .catch(error => {
        console.error("There was an error fetching the employees!", error);
      });
  };

  useEffect(() => {
    fetchEmployees();
  }, []);

  const handleAddEmployee = (newEmployee) => {
    axios.post('http://localhost:5000/employee', newEmployee)
      .then(response => {
        setEmployees([...employees, response.data]);
      })
      .catch(error => {
        console.error("There was an error adding the employee!", error);
      });
  };

  const handleUpdateEmployee = (id, updatedEmployee) => {
    axios.put(`http://localhost:5000/employee/${id}`, updatedEmployee)
      .then(response => {
        setEmployees(employees.map(emp => (emp.id === id ? response.data : emp)));
      })
      .catch(error => {
        console.error("There was an error updating the employee!", error);
      });
  };

  const handleDeleteEmployee = (id) => {
    axios.delete(`http://localhost:5000/employee/${id}`)
      .then(response => {
        setEmployees(employees.filter(emp => emp.id !== id));
      })
      .catch(error => {
        console.error("There was an error deleting the employee!", error);
      });
  };

  return (


      <div className="App">
        <h1>Employee Management System</h1>
        <nav>
          <Link to="/">Home</Link>
          <Link to="/add">Add Employee</Link>
          <Link to="/update">Update Employee</Link>
        </nav>
        <EmployeeList employees={employees} onDeleteEmployee={handleDeleteEmployee} />
      </div>

  );
}

export default App;
